import './App.css';
import Typewriter from 'typewriter-effect';
import {useState} from "react";
import Axios from 'axios';

function App() {

  const [nom, setNom] = useState('');

  const addArgo = () => {
    Axios.post('http://localhost:3001/create', {name: nom}).then(() => {
      console.log('Success')
    })
  }


  return (
    <div className="App">

      <header className="header">
        <img src="https://www.wildcodeschool.com/assets/logo_main-e4f3f744c8e717f1b7df3858dce55a86c63d4766d5d9a7f454250145f097c2fe.png" alt="Wild Code School logo" />
        <h1>DevChallenge</h1>
      </header>

      <div className="accueil">
        <div className="animTxt">
          <Typewriter
            options={{
              loop:true, 
              deleteSpeed:15
            }}
            onInit={(animTxt) => {
              animTxt.changeDelay(40)
              .typeString('Bienvenue <strong>Jeune <span style="color: #ffd700">Argonaute</span> ! </strong>')
              .pauseFor(500)
              .typeString('<br>Descends pour rejoindre l\'Equipage')
              .pauseFor(800)
              .deleteChars(34)
              .pauseFor(200)
              .typeString('Aides-nous à trouver la Toison d\'or !')
              .pauseFor(800)
              .deleteAll()
              .pauseFor(800)
              .start();
            }}
          />
        </div>
      </div>

      <div className="add">
        <h2>Prends part à l'Aventure !</h2>
        <label>Entres ton Nom : </label>
        <br />
        <input type="text" placeholder="Ex: Marco Paulo.." onChange={(event) => {
          setNom(event.target.value)
        }} />
        <br /><br />
        <button className="btn" onClick={addArgo}>Rejoindre l'équipage</button>
      </div>

      <hr />

      <div className="show">
        <h2>Membres de l'équipage</h2>
        <button className="btn">Afficher l'équipage</button>
      </div>

      <footer className="footer">
        <p>Réalisé par Jason en Anthestérion de l'an 515 avant JC</p>
      </footer>

    </div>
  );
}

export default App;
